package strings_and_dialogs;

import javax.swing.JOptionPane;

public class KnockKnock {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 JOptionPane.showMessageDialog(null, "Knock Knock");

 JOptionPane.showMessageDialog(null,  "Who is there?");
 
 JOptionPane.showMessageDialog(null,  "Egg");
 
 JOptionPane.showMessageDialog(null,  "Egg who?");
 
 JOptionPane.showMessageDialog(null,  "Just eggcited to meet you");
 
 
 
	}

}
